function eyeDom = getEyeDom(vpcode)%% asks for the participant's eye dominance% input: vpcode (string, name of subject) % % 2006 by Martin Rolfs% 2016 by Richard Schpause(0.5);FlushEvents('keyDown');% read eyeDomeyeDom = input(['>>>> Dominant Eye (0 = LEFT_EYE, 1 = RIGHT_EYE) of "' vpcode '"\n --> '], 's');% check whether eyeDom is any of the allowed valuesif strcmp(eyeDom, '0') || strcmp(eyeDom, '1')    eyeDom = str2num(eyeDom);else    disp(['Eye Dominance was not specified or is not 0 or 1. Thus using ' num2str(0) ' as Eye Dominance.']);    eyeDom = 0;end