function getKeyAssignment%% asks for key assignment%% 2008 by Martin Rolfsglobal setting keysKbName('UnifyKeyNames');% Target keys for response%keys.respButtons = [KbName('b') KbName('n')];        keys.respButtons = [KbName('LeftArrow'), KbName('RightArrow')];keys.nextTrial = KbName('space');               % space bar keys.resLeft  = keys.respButtons(1);keys.resRight    = keys.respButtons(2);keys.EscapeFromExp = KbName('q');%keys.resUpLeft    = keys.respButtons(3);%keys.resUpRight   = keys.respButtons(4);FlushEvents('keyDown');