function vpcode = getVpCode(exptstr)%% asks for subject-ID% input: exptstr (string) name of experiment % % 2006 by Martin Rolfs% 2022 edited for 2 digits by Nicolas Rothif nargin==1	FlushEvents('keyDown');	vpnr = input('>>>> One or two digit subject code [e.g., 42]:  ','s');	if length(vpnr)==1		vpnr = strcat('0', vpnr);    elseif length(vpnr) > 2        FlushEvents('keyDown');        vpnr = input('>>>> TOO LONG! Again: 1-2 digit subject code [e.g., 42]:  ','s');	end		if ~ischar(exptstr) || isempty(vpnr)		vpcode = sprintf(strcat(exptstr,'%s'), 'tst');	else		vpcode = sprintf(strcat(exptstr,'%s'), vpnr);	endelse	fprintf(1, 'use 1 string argument during call to getVpCode\n');	vpcode=NaN;end