function keyPress = perfFeedback(str,x,y)global scr visualbounds = Screen(scr.main,'TextBounds',str);pt = 0.012*length(str); % presentation timex  = x-bounds(3)/2;     % x positiony  = y-bounds(4)/2;     % y positionc  = visual.fgColor(1); % colorWaitSecs(0.2);nBlank = round(0.20*1/scr.fd); % fade out takes 0.2 secsrubber([]);Screen(scr.main,'Drawtext',str,x,y,c);vbl = Screen(scr.main,'Flip');keyPress = 0;while ~keyPress    [keyPress, dummy] = checkTarPress(1:231);   % accept all buttonsendWaitSecs(round(0.2/scr.fd)*scr.fd - scr.fd);for i = 1:nBlank	c = round(visual.fgColor(1)+(visual.bgColor(1)-visual.fgColor(1))*i/nBlank);	 % rubber([x y x+width y+setting.FONT_SIZE]); %changed const to setting	 % (OS)	Screen(scr.main,'Drawtext',str,x,y,c);	    Screen(scr.main,'Flip',vbl + (i-0.5)*scr.fd);endrubber([]);Screen(scr.main,'Flip');