function reddUp%% closes screens, shuts down eyelink, clears working memory% % by Martin Rolfs, edited by Richard Schweitzerglobal setting data design visual scr keys% re-enable keyboardListenChar(1);% get eyelink data file on subject computerif setting.TEST==0    status = Eyelink('ReceiveFile'); %,edffilename, edffilename, 'Data/');    if status == 0        fprintf(1,'\n\nFile transfer was cancelled\n\n');    elseif status < 0        fprintf(1,'\n\nError occurred during file transfer\n\n');    else        fprintf(1,'\n\nFile has been transferred (%i Bytes)\n\n', status)    end    % move to EDF folder    [sucMov, messMov, messMovID] = movefile(setting.edffilename, 'Edf/', 'f');    if sucMov        fprintf('File successfully moved into data folder.\n');    else        fprintf('File was not moved into data folder!\n');        fprintf([messMovID, ': ', messMov, '\n']);    endend% shut down Eyelinkif setting.TEST==0    Eyelink('closefile');    WaitSecs(2.0);     Eyelink('shutdown');    WaitSecs(2.0);end% close screenShowCursor;Screen('CloseAll');if setting.Pixx==2    Datapixx('Close');end% save mat filedisp([num2str(GetSecs) ' Saving mat file...']);save(setting.matFile, 'data', 'design', 'visual', 'scr', 'keys', 'setting');home;