function [pressed, time] = waitTarPress(targets);% Waits for button presses specified in targets, which may be N multiple keys% Specify different targets in different entries of a string array% Returns whether buttons were pressed (1 to N if true, 0 otherwise)%% 2006 by Martin Rolfskey = 1000;while ismember(key,targets)==0	FlushEvents('keyDown');	KbWait;	[keyIsDown, t, keyCode] = KbCheck;    key = find(keyCode);endpressed = [];if length(key) == 1	pressed = find(targets==key);    time    = t;endif isempty(pressed)	pressed = 0;    time    = [];end	